define(['jquery','pagebuilderRegister'],function($,pagebuilderRegister){const defaultConfig={colors:['red','green','yellow','black'],$element:$};pagebuilderRegister.widgetRegister('hello-world',function(args){setInterval(function(){let color=args.colors[Math.random()*args.colors.length|0];args.$element.find('.hello-world').css('color',color);},1000);},defaultConfig);})